import React,{useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { fetchDogById } from "../../redux/action/actions";
const DetailPage = ()=>{

    const dispatch = useDispatch();
    const { id } = useParams();
    const [dog, setDog] = useState({});
    const sDog = useSelector((state) => state.dog);
  
    useEffect(() => {
      dispatch(fetchDogById(id)).then((res) => {
        setDog(res.payload);
      });
    }, [dispatch, id]);


    return(
      <div className="containter">
        <h1>Dog Details </h1>
        <div className="detail">
          <img className="image" src={sDog.img} alt={sDog.name} />
          <h3>{sDog.name}</h3>
          <h5>Height: {sDog.height}</h5>
          <h5>Weight: {sDog.weight}</h5>
          <h5>Life span: {sDog.lifeSpan}</h5>
          <h6>Temperaments: {sDog.temperament}</h6>
        </div>
          
      </div>
      )
}


export default DetailPage
