import React,{useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { fetchDogById } from "../../redux/action/actions";
import DetailedDog from "../../components/dogDetail";
const DetailPage = ()=>{

    const dispatch = useDispatch();
    const { id } = useParams();
    const [dog, setDog] = useState({});
    const allDogs = useSelector((state) => state.detail)
  
    useEffect(() => {
      dispatch(fetchDogById(id)).then((e)=>{
        setDog(e.payload)
      });
      console.log(dog)
      ;
    }, [dispatch, id]);


    return(
      <div>
        {Object.entries(dog).length === 0 ? (
        <div className={s.background}>
          <div className={s.back}>
            <Link to="/home">
              <img
                src={iconArrow}
                alt="Back"
                height="50px"
                width="50px"
                style={{ marginLeft: "50px" }}
              />
            </Link>
            <p className={s.return}>Return</p>
          </div>
          <div className={s.gifLoading}>
            <img
              style={{ width: "200px", height: "200px" }}
              src={gif}
              alt="LoadingGif"
            />
            <p>Wait a second</p>
          </div>
        </div>
      ) : (
        <div>
          <div>

            <p>Return</p>
          </div>
          <div>
            <div>
              <img src={dog.img} alt={dog.name} />
            </div>
            <div >
              <h2 >{dog.name}</h2>
              <div>
                <h3>Weight: </h3>
                <p>
                  {dog.weight} Kg
                </p>
              </div>

              <div >
                <h3>Height: </h3>
                <p>
                  {dog.height} Cm
                </p>
              </div>

              <div>
                <h3>Life Span: </h3>
                <p>{dog.lifeSpan}</p>
              </div>
              <p>
                {Array.isArray(dog.temperament)
                  ? dog.temperament.map((e) => e.name).join(", ")
                  : dog.temperament}
              </p>
            </div>
          </div>
        </div>
      )}
      </div>
    )
}


export default DetailPage
