import React,{useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import { useDispatch } from "react-redux";
import { fetchDogById } from "../../redux/action/actions";
const DetailPage = ()=>{

    const dispatch = useDispatch();
    const { id } = useParams();
    const [dog, setDog] = useState({});
    
  
    useEffect(() => {
      dispatch(fetchDogById(id)).then((res) => {
        setDog(res.payload);
      });
    }, [dispatch, id]);


    return(
      <div className="containter">
        <h1>Dog Details </h1>
        <div className="detail">
          <img className="image" src={dog.img} alt={dog.name} />
          <h3>{dog.name}</h3>
          <h5>Height: {dog.height}</h5>
          <h5>Weight: {dog.weight}</h5>
          <h5>Life span: {dog.lifeSpan}</h5>
          <h6>Temperaments: {dog.temperament}</h6>
        </div>
          
      </div>
      )
}


export default DetailPage
