import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { fetchTemperaments, createDog } from "../../redux/action/actions"

const validate = (input) => {
    let errors = {};
    let expression = /^[a-zA-Z ]+$/gm;

    if (!input.name) {
        errors.name = "Name is required";
    }
    if (!input.heightMax || !input.heightMin) {
        errors.height = "Height is required";
    }
    if (!input.weightMax || !input.weightMin) {
        errors.weight = "Weight is required";
    }
    if (!input.img) {
        errors.img = "Image is required";
    }

    if (parseInt(input.name)) {
        errors.name = "Name is invalid, write a text";
    } else if (!expression.test(input.name)) {
        errors.name = "Special caracters aren't supported";
    }

    if (!input.age) {
        errors.age = "Life span is required";
    } else if (input.age > 20 || input.age < 1) {
        errors.age = " life span must be in a number from 1 - 20";
    }

    if (Number(input.weightMin) <= 0 || Number(input.weightMin >= 100)) {
        errors.weightMin = "Minimum heigh must be in a number from 0 - 100";
    }
    if (Number(input.weightMax) <= 0 || Number(input.weightMax > 100)) {
        errors.weightMax = "Maximun weight must be in a number from 0 - 150";
    }
    if (Number(input.heightMin) <= 0 || Number(input.heightMin) >= 100) {
        errors.heightMin = "Minimun height must be in a number from 0 - 100";
    }
    if (Number(input.heightMax) <= 0 || Number(input.heightMax) > 100) {
        errors.heightMax = "Maximun height must be in a number from 0 - 100";
    }
    if (!input.temperament.length) {
        errors.temperament = "Select at least 1 temperament";
    }
    return errors;
};

export const DogsForm = () => {
    const dispatch = useDispatch();
    const history = useHistory();
    const temperaments = useSelector((state) => state.temperaments);
    const [errors, setErrors] = useState("");
    const [input, setInput] = useState({
        name: "",
        age: "",
        img: "",
        temperament: [],
        weightMin: "",
        weightMax: "",
        heightMin: "",
        heightMax: "",
    });

    useEffect(() => {
        dispatch(fetchTemperaments());
    }, [dispatch]);

    function handleChange(e) {
        setInput({
            ...input,
            [e.target.name]: e.target.value,
        });
        setErrors(
            validate({
                ...input,
                [e.target.name]: e.target.value,
            })
        );
    }

    function handleSelect(e) {
        if (input.temperament.find((t) => t.id === e.target.value.split(",")[0])) {
            console.log({ input });
            alert("Already in the list");
        } else {
            setInput({
                ...input,
                temperament: [
                    ...input.temperament,
                    {
                        id: e.target.value.split(",")[0],
                        name: e.target.value.split(",")[1],
                    },
                ],
            });
        }
    }

    const handleDelete = (e) => {
        setInput({
            ...input,
            temperament: input.temperament.filter((el) => el !== e),
        });
    };

    function handleSubmit(e) {
        if (
            input.name &&
            !parseInt(input.name) &&
            input.age &&
            input.weightMin &&
            input.weightMax &&
            input.heightMin &&
            input.heightMax &&
            input.img &&
            input.temperament &&
            input.temperament.length > 0
        ) {
            e.preventDefault();
            dispatch(
                createDog({
                    name: input.name,
                    age: input.age,
                    img: input.img,
                    heightMax: input.heightMax,
                    heightMin: input.heightMin,
                    weightMax: input.weightMax,
                    weightMin: input.weightMin,
                    temperament: input.temperament.map((t) => Number(t.id)),
                })
            );

            alert("Success! Your dog was created");
            setInput({
                name: "",
                age: "",
                weightMin: "",
                weightMax: "",
                heightMin: "",
                heightMax: "",
                img: "",
                temperament: [],
            });
            history.push("/home");
        } else {
            alert("incomplete or wrong information");
            e.preventDefault();
        }
    }

    return (
        <div>
                <Link to="/home">
                Return
                </Link>
            <h2>CREATE YOUR OWN DOG</h2>
            <form
                onSubmit={(e) => {
                    handleSubmit(e);
                }}
            >
                {/* Inputs */}
                <div>
                    {/* BreedName */}
                    <div style={{ width: "352px" }}>
                        <input
                            style={{ width: "352px" }}
                            type="text"
                            placeholder="Breed name"
                            value={input.name}
                            name="name"
                            onChange={(e) => handleChange(e)}
                        />
                    </div>
                    <br />
                    {/* life span */}
                    <div style={{ width: "352px" }}>
                        <input
                            style={{ width: "352px" }}
                            type="number"
                            placeholder="Life span"
                            value={input.age}
                            name="age"
                            onChange={(e) => handleChange(e)}
                        />
                    </div>
                    <br />
                    <div>
                        {/* heightMax */}
                        <div>
                            <input
                                type="number"
                                min="0"
                                placeholder="Maximun height"
                                value={input.heightMax}
                                name="heightMax"
                                onChange={(e) => handleChange(e)}
                            />
                        </div>
                        {/* heightMin */}
                        <div>
                            <input
                                type="number"
                                min="0"
                                placeholder="Minimum height"
                                value={input.heightMin}
                                name="heightMin"
                                onChange={(e) => handleChange(e)}
                                style={{ marginLeft: "10px" }}
                            />
                        </div>
                    </div>
                    <br />
                    <div>
                        {/* weightMax */}
                        <div>
                            <input
                                type="number"
                                min="0"
                                placeholder="Maximun weight"
                                value={input.weightMax}
                                name="weightMax"
                                onChange={(e) => handleChange(e)}
                            />
                        </div>
                        <br />
                        {/* weightMin */}
                        <div>
                            <input
                                type="number"
                                min="0"
                                placeholder="Minimum weight"
                                value={input.weightMin}
                                name="weightMin"
                                onChange={(e) => handleChange(e)}
                                style={{ marginLeft: "10px" }}
                            />
                        </div>
                    </div>
                    <br />
                    {/* Image */}
                    <div style={{ width: "352px" }}>
                        <input
                            style={{ width: "352px" }}
                            type="URL"
                            placeholder="Url of image"
                            value={input.img}
                            name="img"
                            onChange={(e) => handleChange(e)}
                        />
                    </div>
                    <br />
                    {/* Temperament */}
                    <div>
                        <select onChange={(e) => handleSelect(e)}>
                            {temperaments?.map((el, i) => (
                                <option value={`${el.id},${el.name}`} key={i}>
                                    {el.name}
                                </option>
                            ))}
                        </select>
                    </div>
                </div>
            </form>

            <div>
                {input.temperament.map((el, i) => (
                    <button
                        key={i}
                        type="reset"
                        onClick={() => handleDelete(el)}
                    >
                        {el.name} X
                    </button>
                ))}
                {errors.temperament && <p className="error">{errors.temperament}</p>}
            </div>

            <div>
                <button
                    type="submit"
                    onClick={(e) => handleSubmit(e)}
                >
                    Create breed
                </button>
            </div>
            {/*Errors */}
            <div>
                <ul>
                    {errors.name && <li className="error">{errors.name}</li>}

                    {errors.age && <li className="error">{errors.age}</li>}

                    {(errors.weight && <li className="error">{errors.weight}</li>) ||
                        (errors.weightMax && (
                            <li className="error">*{errors.weightMax}</li>
                        )) ||
                        (errors.weightMin && <li className="error">{errors.weightMin}</li>)}

                    {(errors.height && <li className="error">{errors.height}</li>) ||
                        (errors.heightMax && (
                            <li className="error">{errors.heightMax}</li>
                        )) ||
                        (errors.heightMin && <li className="error">{errors.heightMin}</li>)}

                    {errors.img && <li className="error">{errors.img}</li>}
                </ul>
            </div>
        </div>
    )
}
