import {
    fetchDogs,
    fetchTemperaments,
    filterByTemperaments
} from "../../redux/action/actions"
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import DogsCards from "../../components/dogCards";

export const HomePage = ()=>{
    const dispatch = useDispatch();
    const allDogs = useSelector((state) => state.dogs);

    const [currentPage, setCurrentPage] = useState(1);
    const dogsCardsPerPage = 8;
    const numberOfLastDog = currentPage * dogsCardsPerPage;
    const numberOfFirstDog = numberOfLastDog - dogsCardsPerPage;
    const currentDogs = allDogs.slice(numberOfFirstDog, numberOfLastDog);
    const paginado = (pageNumber) => {
      setCurrentPage(pageNumber);
    };
    
     useEffect(() => {;
         dispatch(fetchDogs());
         dispatch(fetchTemperaments());
         dispatch(filterByTemperaments());
       }, [dispatch]);



    return(
        <div className="mt-5">
            <h1>Breed list</h1>
            <hr/>
            <table className="table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Temperament</th>
                        <th>Weight</th>
                        <th>Image</th>
                    </tr>
                    {currentDogs.length === 0 ? (
                <img alt="loadingGif"/>
                ) : (
                currentDogs?.map((el) => {
                return (
                  <DogsCards
                    key={el.id}
                    id={el.id}
                    name={el.name}
                    img={el.img}
                    temperament={el.temperament}
                    weight={el.weight}
                  />
                );
              })
            )}
                </thead>
            </table>
        </div>
    )
}
