import React,{useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { fetchDogById } from "../../redux/action/actions";
import DetailedDog from "../../components/dogDetail";
const DetailPage = ()=>{

    const dispatch = useDispatch();
    const { id } = useParams();
    const [dog, setDog] = useState({});
    const allDogs = useSelector((state) => state.detail)
  
    useEffect(() => {
      dispatch(fetchDogById(id)).then((e)=>{
        setDog(e.payload)
      });
      ;
    }, [dispatch, id]);


    return(
      <div className="containter">
          <h1>Dog Details </h1>
          <div className="detail">
            <img className="image" src={allDogs.img} alt={name} />
            <h3>{name}</h3>
            <h5>Height: {height}</h5>
            <h5>Weight: {weight}</h5>
            <h5>Life span: {lifeSpan}</h5>
            <h6>Temperaments: {temperament}</h6>
          </div>
            
        </div>
    )
}


export default DetailPage
